var script_f7_28_7629=[""].join("\n");
var outline_f7_28_7629=null;
var title_f7_28_7630="Gaucher treatment goals A";
var content_f7_28_7630=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F75418%7EPEDS%2F54387&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=13\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F75418%7EPEDS%2F54387&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=13\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Therapeutic goals in the treatment of Gaucher disease - part 1",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Manifestation",
"       </td>",
"       <td class=\"subtitle1\">",
"        Goals",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\" rowspan=\"1\">",
"        Anemia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" colspan=\"1\" rowspan=\"1\">",
"        <p>",
"         Hgb &lt;12 g/dL in males &gt;12 years",
"        </p>",
"        <p>",
"         Hgb &lt;11 g/dL in females &gt;12 years",
"        </p>",
"        <p>",
"         Hgb &lt;10.5 g/dL in children 2 to 12 years",
"        </p>",
"        <p>",
"         Hgb &lt;9.5 g/dL in children 6 mos to 2 years",
"        </p>",
"        <p>",
"         Hgb &lt;10.1 g/dL in children &lt; 6 mos",
"        </p>",
"       </td>",
"       <td colspan=\"1\" rowspan=\"1\">",
"        <p>",
"         Increase Hgb within 12 to 24 months:",
"        </p>",
"        <p>",
"         to &ge;11",
"g/dL for children and females &gt;12 years",
"        </p>",
"        <p>",
"         to &ge;12",
"g/dL for males &gt;12 years",
"        </p>",
"        <p>",
"         Eliminate dependency on blood",
"transfusions",
"        </p>",
"        <p>",
"         Reduce fatigue, dyspnea, angina",
"        </p>",
"        <p>",
"         Maintain improved Hgb values",
"achieved after the first 12 to 24 months of therapy",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\" rowspan=\"1\">",
"        Thrombocytopenia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" colspan=\"1\" rowspan=\"1\">",
"        <p>",
"         Moderate: platelet count between 60,000 and",
"120,000/microL",
"        </p>",
"        <p>",
"         Severe: platelet count &lt;60,000",
"        </p>",
"        <p>",
"         Risk for surgical or obstetrical bleeding: platelet",
"count &lt;30,000/microL",
"        </p>",
"       </td>",
"       <td colspan=\"1\" rowspan=\"1\">",
"        <p>",
"         All",
"patients:",
"        </p>",
"        <p>",
"         Increase platelet count in the first",
"12 months of treatment to prevent surgical, obstetrical, and",
"spontaneous bleeding",
"        </p>",
"        <p>",
"         Patients",
"with splenectomy:",
"        </p>",
"        <p>",
"         Normalization of platelet count by",
"one year of treatment",
"        </p>",
"        <p>",
"         Patients with intact spleen:",
"        </p>",
"        <p>",
"         Moderate thrombocytopenia: increase",
"the platelet count by 1.5- 2.0-fold in first year; approach low normal",
"level by second year",
"        </p>",
"        <p>",
"         Severe thrombocytopenia:",
"        </p>",
"        <p>",
"         Increase the platelet count by 1.5 fold during the",
"first year and",
"continue to increase slightly during the second through fifth years;",
"double platelet count by second year; complete normalization is not",
"expected",
"        </p>",
"        <p>",
"         Avoid splenectomy",
"        </p>",
"        <p>",
"         Maintain stable platelet counts to",
"eliminate risks of bleeding after a maximal response has been achieved.",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\" rowspan=\"1\">",
"        Hepatomegaly",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" colspan=\"1\" rowspan=\"1\">",
"        <p>",
"         Moderate: Liver mass &gt;1.25 times the normal 2.5",
"percent of total body weight in kg (assessed by quantitative imaging,",
"preferably MRI)",
"        </p>",
"        <p>",
"         Severe: Liver mass &gt;2.5 times",
"normal volume",
"        </p>",
"       </td>",
"       <td colspan=\"1\" rowspan=\"1\">",
"        <p>",
"         Reduce and maintain the liver volume",
"to 1.0 to 1.5 times normal volume",
"        </p>",
"        <p>",
"         Reduce liver volume by 20 to 30",
"percent within the first two years of treatment and by 30 to 40 percent",
"by the third to fifth year",
"        </p>",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    This table is continued.",
"    <div class=\"footnotes\">",
"     Hbg: hemoglobin; MRI: magnetic resonance imaging.",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from Pastores GM, Weinreb NJ, Aerts H, et al. Therapeutic goals in the treatment of Gaucher disease. Semin Hematol 2004; 41:4, Baldellou A, Andria G, Campbell PE, et al. Paediatric non- neuronopathic Gaucher disease: recommendations for treatment and monitoring. Eur J Pediatr 2004; 163:67.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Therapeutic goals in the treatment of Gaucher disease - part 2",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1\">",
"       Manifestation",
"      </td>",
"      <td class=\"subtitle1\">",
"       Goals",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_left\" colspan=\"2\" rowspan=\"1\">",
"       Splenomegaly",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       <p>",
"        Splenic mass &gt; the normal 0.2 percent of total body weight in kg.",
"       </p>",
"       <p>",
"        Moderate: &gt;5 and &le;15 times normal volume",
"       </p>",
"       <p>",
"        Severe: &gt;15 times normal volume (assessed by quantitative imaging, preferably MRI)",
"       </p>",
"      </td>",
"      <td>",
"       <p>",
"        Reduce and maintain spleen volume to &le;2 to times normal",
"       </p>",
"       <p>",
"        Reduce the spleen volume by 30 to 50 percent within the first year and to 50 to 60 percent by the second to fifth years",
"       </p>",
"       <p>",
"        Alleviate symptoms due to splenomegaly (abdominal distension, early satiety, new splenic infarction)",
"       </p>",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_left\" colspan=\"2\" rowspan=\"1\">",
"       Skeletal pathology",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       <p>",
"        Focal disease: Lytic and sclerotic lesions associated with infarction, thrombosis, and inflammatory processes that can progress to osteonecrosis",
"       </p>",
"       <p>",
"        Local disease: remodeling defects of the long bones and long bone deformities",
"       </p>",
"       <p>",
"        Generalized osteopenia and osteoporosis",
"       </p>",
"      </td>",
"      <td>",
"       <p>",
"        All patients:",
"       </p>",
"       <p>",
"        Retain skeletal function by preventing the onset of new skeletal complications",
"       </p>",
"       <p>",
"        Lessen or eliminate bone pain within first or second year",
"       </p>",
"       <p>",
"        Prevent bone crises or decrease frequency",
"       </p>",
"       <p>",
"        Prevent osteonecrosis and subchondral joint collapse",
"       </p>",
"       <p>",
"        Improve bone mineral density",
"       </p>",
"       <p>",
"        In children:",
"       </p>",
"       <p>",
"        Attain normal or ideal peak skeletal mass",
"       </p>",
"       <p>",
"        Increase cortical and trabecular BMD by second year",
"       </p>",
"       <p>",
"        In adults:",
"       </p>",
"       <p>",
"        Increase trabecular BMD by 3 to 5 years",
"       </p>",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_left\" colspan=\"2\" rowspan=\"1\">",
"       Growth",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       &nbsp;",
"      </td>",
"      <td>",
"       <p>",
"        Normalize growth such that child achieves a normal height according to population and family history by the third year of treatment",
"       </p>",
"       <p>",
"        Achieve normal onset of puberty",
"       </p>",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_left\" colspan=\"2\" rowspan=\"1\">",
"       Pulmonary involvement",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       &nbsp;",
"      </td>",
"      <td>",
"       <p>",
"        Reverse hepatopulmonary syndrome and dependency on oxygen",
"       </p>",
"       <p>",
"        Ameliorate pulmonary hypertension (ERT plus other therapies)",
"       </p>",
"       <p>",
"        Improve functional status and quality of life",
"       </p>",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_left\" colspan=\"2\" rowspan=\"1\">",
"       Functional health and well-being",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       &nbsp;",
"      </td>",
"      <td>",
"       <p>",
"        Improve or restore physical function for carrying out normal daily activities and fulfilling functional roles",
"       </p>",
"       <p>",
"        Improve scores from baseline of validated quality-of-life-instruments within the second or third year, depending upon disease burden",
"       </p>",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Data from:",
"     <br>",
"      Pastores GM, Weinreb NJ, Aerts H, et al. Therapeutic goals in the treatment of Gaucher disease. Semin Hematol 2004; 41:4.",
"      <br>",
"       Baldellou A, Andria G, Campbell PE, et al. Paediatric non-neuronopathic Gaucher disease: recommendations for treatment and monitoring. Eur J Pediatr 2004; 163:67.",
"      </br>",
"     </br>",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
