" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Lyme disease: Clinical manifestations in children",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?7/3/7225/contributors\">",
"     Author",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?7/3/7225/contributors\">",
"     Eugene D Shapiro, MD, FAAP",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?7/3/7225/contributors\">",
"     Section Editor",
"    </a>",
"    <br/>",
