" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Malignancy-associated gastroparesis: Pathophysiology and management",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?7/3/7226/contributors\">",
"     Authors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?7/3/7226/contributors\">",
"     Mehnaz A Shafi, MD",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?7/3/7226/contributors\">",
"     Eiad Nasser, MD",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?7/3/7226/contributors\">",
"     Milind Javle, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?7/3/7226/contributors\">",
"     Section Editors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?7/3/7226/contributors\">",
"     Reed E Drews, MD",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?7/3/7226/contributors\">",
"     Eduardo Bruera, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?7/3/7226/contributors\">",
"     Deputy Editor",
"    </a>",
