var title_f7_3_7227="Limitations of provocative testing for GH deficiency";
var content_f7_3_7227=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ENDO%2F51347&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ENDO%2F51347&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Limitations of provocative testing for growth hormone deficiency in children",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td>",
"        Pharmacological testing is not physiologic",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Normality is arbitrarily defined, if defined at all",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Reproducibility in both normal and abnormal children is poor, even when the same test is performed",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Age and sex steroid hormone status affect the response",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Nutritional adequacy and body composition, particularly, adiposity, can affect response",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        There is no standard serum growth hormone assay, and there are large&nbsp;interassay variations among the assays&nbsp;in use",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        All the tests are costly",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
