var title_f7_30_7659="Functional exercise progression for return to tennis";
var content_f7_30_7659=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=EM%2F60005&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=EM%2F60005&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Functional exercise progression for return to tennis",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td>",
"       1. Mini-tennis from service line, essentially point of contact &rarr; follow through",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       2. 2/3 court with a little more backswing and gentle spin if able",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       3. Full court with good leg drive but no heavy spin or pace",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       4. Throwing tennis ball from service line to other side",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       5. Serves from service line",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       6. Full groundstrokes &rarr; full serves &rarr; competition",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       7. Limit excessive/early spin; limit volume of play for 2 weeks until functioning normally",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    The progression described above is approximate. Steps one through five are performed 10 minutes daily for several days before moving to the next step.",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
