" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Immunomodulator therapy in Crohn's disease",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?7/31/7673/contributors\">",
"     Author",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?7/31/7673/contributors\">",
"     Richard P MacDermott, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?7/31/7673/contributors\">",
"     Section Editor",
"    </a>",
