var title_f7_35_7741="Use of the varicella vaccine";
var content_f7_35_7741=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ID%2F74296&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ID%2F74296&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Recommendations for the use of the varicella vaccine",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Persons &lt;13 years of age",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"1\">",
"        Varicella vaccine has been approved",
"for use among all healthy children between 12 months and 12 years of age.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        12 to 18 months of age",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        All children should be vaccinated routinely at 12 to 18",
"months of age.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" colspan=\"1\">",
"        Varicella vaccine should optimally",
"be administered to a child at the same time as the MMR vaccine.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        19 months to 12 years",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" colspan=\"1\">",
"        Proof of varicella vaccination or",
"natural infection should be provided before entry into child care or elementary school.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" colspan=\"1\">",
"        Varicella vaccine is recommended for",
"all children by their 13th birthday; proof of vaccination or natural infection",
"should also be considered for children entering middle school.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Persons &ge;13 years of age",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Varicella vaccine is approved for use among healthy",
"adolescents and adults.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" colspan=\"1\">",
"        Persons &gt;13 years should",
"receive two 0.5 mL doses of vaccine subcutaneously, four to eight weeks apart.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        High-risk groups",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" colspan=\"1\">",
"        Persons who live or work in settings",
"where transmission of VZV is probable (eg, teachers of young children, day care",
"workers, employees of residential care institutions)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Persons who live or work in settings where transmission",
"of VZV can occur (eg, college students, inmates and employees of correctional",
"facilities, military personnel)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Women of childbearing age who are not pregnant",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Adolescents and adults living in households with",
"children",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        International travelers",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Health care workers",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Household contacts of",
"immunocompromised persons",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" colspan=\"1\">",
"        Immunocompromised hosts are at high",
"risk (30 percent) for disseminated varicella.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" colspan=\"1\">",
"        Vaccination of healthy household",
"contacts protects immunocompromised patients by decreasing the likelihood of",
"introduction of wild-type virus into the household.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Immunosuppressed hosts",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        The varicella vaccine is",
"        <strong>",
"         not",
"        </strong>",
"        approved for persons with any underlying malignancy including blood dyscrasias,",
"lymphoma, leukemia*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Varicella vaccine is",
"        <strong>",
"         not",
"        </strong>",
"        currently approved for patients with HIV-infection; the use of varicella vaccine in HIV-infected",
"children is being evaluated",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Varicella vaccine is",
"        <strong>",
"         not",
"        </strong>",
"        approved for patients with cellular immunodeficiencies",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * Children/adolescents between the ages of 12 months and 17 years who have acute lymphocytic leukemia (ALL) in remission for at least 12 continuous months, peripheral lymphocyte count &gt;700/mm3, and have a platelet count &gt;100,000/mm3 within 24 hours of vaccination may obtain varicella vaccine through a research protocol.",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from the Centers for Disease Control and Prevention. Prevention of varicella: Update recommendations of the advisory committee on immunization practices. MMWR Morb Mortal Wkly Rep 1999; 48 (RR-6):1.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
