var title_f7_39_7804="Dx tests small bowel tumors";
var content_f7_39_7804=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=GAST%2F62087&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=GAST%2F62087&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Comparison of diagnostic tests used in the evaluation of suspected small bowel tumors",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <colgroup span=\"3\" width=\"33%\">",
"     </colgroup>",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Diagnostic test",
"       </td>",
"       <td class=\"subtitle1\">",
"        Advantages",
"       </td>",
"       <td class=\"subtitle1\">",
"        Disadvantages",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Plain abdominal film",
"       </td>",
"       <td>",
"        May show obstruction",
"       </td>",
"       <td>",
"        Nonspecific",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Upper gastrointestinal series/small bowel follow through",
"       </td>",
"       <td>",
"        May show mass lesion, mucosal defect, or intussusception",
"       </td>",
"       <td>",
"        No visualization outside lumen; not helpful in staging",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Enteroclysis (CT, MRI, or conventional)",
"       </td>",
"       <td>",
"        More sensitive than conventional small bowel follow through",
"       </td>",
"       <td>",
"        May require a duodenal tube",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        CT scan",
"       </td>",
"       <td>",
"        Allows staging; may aid in diagnosis of tumor type",
"       </td>",
"       <td>",
"        Lacks visualization of lumen or mucosal surface",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Upper endoscopy",
"       </td>",
"       <td>",
"        Direct visualization of mucosal surface of duodenum; allows for biopsy; polypectomy possible",
"       </td>",
"       <td>",
"        Invasive; limited to duodenum",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Push enteroscopy",
"       </td>",
"       <td>",
"        Extends visualization into proximal jejunum; allows for biopsy",
"       </td>",
"       <td>",
"        Invasive; does not permit visualization of the entire small bowel",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Double-balloon enteroscopy",
"       </td>",
"       <td>",
"        Allows visualization of entire small bowel with capacity for biopsy and therapeutic intervention",
"       </td>",
"       <td>",
"        Invasive; not widely available; small risk of pancreatitis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Wireless video capsule endoscopy",
"       </td>",
"       <td>",
"        Noninvasive",
"       </td>",
"       <td>",
"        Does not permit tissue sampling; should not be performed if small bowel obstruction suspected",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
