" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Management of the fetus with compound presentation",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?7/43/7858/contributors\">",
