var title_f7_45_7900="Cutan react multitarg TKI";
var content_f7_45_7900=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ONC%2F74634&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ONC%2F74634&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Cutaneous reactions associated with small molecule tyrosine kinase (TK) inhibitors",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <colgroup width=\"10%\">",
"     </colgroup>",
"     <colgroup width=\"90%\">",
"     </colgroup>",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Drugs",
"       </td>",
"       <td class=\"subtitle1\">",
"        Reported cutaneous effects",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Imatinib",
"       </td>",
"       <td>",
"        Edema, hair repigmentation, hypopigmentation, lichenoid eruption, morbilliform eruption, photosensitization, exacerbation of psoriasis, pruritus, Stevens-Johnson syndrome, xerosis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Dasatinib",
"       </td>",
"       <td>",
"        Acne, HFSR, alopecia, hyperhidrosis, morbilliform eruption, pruritus, urticaria, xerosis, stomatitis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Nilotinib",
"       </td>",
"       <td>",
"        Alopecia, morbilliform eruption, pruritus, xerosis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Bosutinib",
"       </td>",
"       <td>",
"        Acne, allergic dermatitis, erythema, folliculitis, maculopapular rash, pruritic rash, skin exfoliation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Sorafenib",
"       </td>",
"       <td>",
"        HFSR, edema, flushing, stomatitis (mucositis), subungual splinter hemorrhages, squamous hyperproliferation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Sunitinib",
"       </td>",
"       <td>",
"        HFSR, bullous dermatosis, edema, hair depigmentation, yellow skin discoloration, stomatitis (mucositis), periungual erythema, subungual splinter hemorrhages",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Pazopanib",
"       </td>",
"       <td>",
"        Change in hair color, skin hypopigmentation, alopecia, rash, palmar-plantar erythrodysesthesia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Vandetanib",
"       </td>",
"       <td>",
"        Rash, acne, dry skin, dermatitis, pruritus, photosensitivity, HFSR",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Regorafenib",
"       </td>",
"       <td>",
"        HFSR, rash, desquamation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Ponatinib",
"       </td>",
"       <td>",
"        Rash, acneiform eruption, dry skin",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Cabozantinib",
"       </td>",
"       <td>",
"        HFSR, stomatitis, dry mouth",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     HFSR: hand-foot skin reaction.",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
