"}",
"if (Urine_Cr_param.value && Urine_Cr < 0) {",
"Urine_Cr = 0;",
"Urine_Cr_param.value = \"\";",
"clrResults();",
"doCalc = false;",
"alert(\"The minimum value for Urine Cr is 0 mg/dL.\\nIf you are specifying a value with a different unit,\\nchange the unit selector first.\");",
"}",
"if (Urine_Cr_param.value && Urine_Cr > 2000) {",
"Urine_Cr_param.value = \"\";",
"clrResults();",
"Urine_Cr = 0;",
"doCalc = false;",
"alert(\"The maximum value for Urine Cr is 2000 mg/dL.\\nIf you are specifying a value with a different unit,\\nchange the unit selector first.\");",
"}",
"if (Plasma_Cr_param.value && Plasma_Cr < 0) {",
"Plasma_Cr = 0;",
"Plasma_Cr_param.value = \"\";",
"clrResults();",
"doCalc = false;",
"alert(\"The minimum value for Plasma Cr is 0 mg/dL.\\nIf you are specifying a value with a different unit,\\nchange the unit selector first.\");",
"}",
"if (Plasma_Cr_param.value && Plasma_Cr > 20) {",
"Plasma_Cr_param.value = \"\";",
"clrResults();",
"Plasma_Cr = 0;",
"doCalc = false;",
"alert(\"The maximum value for Plasma Cr is 20 mg/dL.\\nIf you are specifying a value with a different unit,\\nchange the unit selector first.\");",
"}",
"",
"",
"}",
"",
"}",
