var title_f7_54_8045="Etiology of dystonia a";
var content_f7_54_8045=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F70477&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F70477&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Etiology of dystonia",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       I. Primary dystonia",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_single\">",
"       A. Sporadic",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_single\">",
"       B. Inherited (all atosomal dominant)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Classic (Oppenheim's) dystonia (common in the Ashkenazi Jews, DYT1 - 9q34)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Childhood- and adult-onset cranial-cervical-limb dystonia (DYT6 - 8p21-22)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Adult-onset cervical and other focal dystonia (DYT7 - 18p)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       II. Dystonia-plus syndromes",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_single\">",
"       A. Sporadic",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Parkinson disease",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Progressive supranuclear palsy",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Multiple system atrophy",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Cortico-basal ganglionic degeneration",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_single\">",
"       B. Inherited",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist2_start\">",
"       1. Autosomal dominant",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist2\">",
"       Dopa-responsive dystonia (DRD) (DYT5-GTP cyclohydrolase I 14q22.1)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist2\">",
"       Dystonia-Myoclonus (alcohol responsive, chromosome - 18)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist2_start\">",
"       2. Autosomal recessive",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist2\">",
"       Tyrosine hydroxylase deficiency (chromosome - 21)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist2\">",
"       Biopterin deficient diseases",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist2\">",
"       Aromatic amino acid decarboxylase deficiency (dopamine-agonist responsive dystonia)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       III. Heredogenerative diseases (Typically not pure dystonia)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_single\">",
"       A. X-linked recessive",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Lubag (X-linked dystonia-parkinsonism, DYT3 - Xq13)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Pelizaeus-Merzbacher disease",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Lesch-Nyhan syndrome",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Deafness, dystonia, retardation, blindness",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_single\">",
"       B. Autosomal dominant",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Rapid-onset dystonia-parkinsonism (RDP)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Juvenile Parkinsonism-Dystonia",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Huntington disease (IT15 - 4p16.3)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Spinocerebellar degenerations (SCA1- SCA8)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Dentato-rubral-pallidoluysian atrophy (DRPLA) vary degeneration with Wernicke's encephalopathy",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Hereditary spastic paraplegia with dystonia",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Thalamo-olivary degeneration with Wernicke's encephalopathy",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_single\">",
"       C. Autosomal recessive",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Wilson disease (Cu-ATPase - 13q14.3)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Neurodegeneration with brain iron accumulation type 1 (NBIA 1) (Hallervorden-Spatz disease - 20p12.3-p13)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Hypoprebetalipoproteinemia, acanthocytosis, retinitis pigmentosa, and pallidal degeneration (HARP syndrome)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Ataxia telangiectasia",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from Jankovic, J, Fahn, S. Dystonic disorders. In: Jankovic, J, Tolosa, E, (Eds), Parkinson's disease and movement disorders, 3rd ed, Williams &amp; Wilkins, Baltimore 1998. p. 513.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
