var title_f7_57_8091="Risks for common diseases";
var content_f7_57_8091=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PC%2F71096&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PC%2F71096&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Risks for common diseases",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1\">",
"       Average risk",
"      </td>",
"      <td class=\"subtitle1\">",
"       Moderate risk",
"      </td>",
"      <td class=\"subtitle1\">",
"       High risk",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       <p>",
"        &bull; No known family history,",
"        <strong>",
"         OR",
"        </strong>",
"       </p>",
"       <p>",
"        &bull; Only one second or more distantly related relative",
"       </p>",
"      </td>",
"      <td>",
"       <p>",
"        &bull; One first degree relative (FDR) with onset of disease at an average age,",
"        <strong>",
"         OR",
"        </strong>",
"       </p>",
"       <p>",
"        &bull; Two second degree relatives (SDR) on the same side of the family*",
"       </p>",
"      </td>",
"      <td>",
"       <p>",
"        &bull; Premature disease or unusual presentation in an FDR",
"       </p>",
"       <p>",
"        &bull; 2 &ge; affected FDRs*",
"       </p>",
"       <p>",
"        &bull; 2 &ge; SDRs, with at least one having premature onset*",
"       </p>",
"       <p>",
"        &bull; 3 &ge; affected relatives*",
"       </p>",
"       <p>",
"        &bull; Moderate risk status on both sides of the family",
"       </p>",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * Relatives must be on the same side of the family.",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
