"      Alcon-Mydril (AR, PY, UY);",
"     </li>",
"     <li>",
"      Colircusi Tropicamida (ES);",
"     </li>",
"     <li>",
"      Losemin (TW);",
"     </li>",
"     <li>",
"      Midric (ID);",
"     </li>",
"     <li>",
"      Minims Tropicamide (GB, IE);",
"     </li>",
"     <li>",
"      Mydramide (IL);",
"     </li>",
"     <li>",
"      Mydriacyl (AE, AU, BF, BH, BJ, BR, CI, CN, CO, CY, DK, EE, EG, ET, GB, GH, GM, GN, HK, HU, IE, IQ, IR, JO, KE, KP, KW, LB, LR, LY, MA, ML, MR, MU, MW, MY, NE, NG, NZ, OM, PE, PH, PK, PL, QA, RU, SA, SC, SD, SE, SG, SL, SN, SY, TH, TN, TW, TZ, UG, VE, YE, ZA, ZM, ZW);",
"     </li>",
"     <li>",
"      Mydriaticum (AT, CH, DE, FR, LU, NL);",
"     </li>",
"     <li>",
"      Mydrin-M (JP);",
"     </li>",
"     <li>",
"      Mydrum (CZ, HN, HU);",
"     </li>",
"     <li>",
"      Oftan Mydrin (PL);",
"     </li>",
"     <li>",
"      Oftan-tropicamid (FI);",
"     </li>",
"     <li>",
"      Runzheng (CL);",
"     </li>",
"     <li>",
"      Sintropic (TW);",
"     </li>",
"     <li>",
