var title_f7_59_8125="Treatment of paraneoplastic syndromes";
var content_f7_59_8125=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ONC%2F54804&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ONC%2F54804&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Paraneoplastic neurologic syndromes: response to treatment",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Syndromes",
"       </td>",
"       <td class=\"subtitle1\">",
"        Treatment",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        Syndromes that often respond to treatment*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Lambert-Eaton myasthenic syndrome",
"       </td>",
"       <td>",
"        IV immunoglobulin; plasma exchange, 2,3 diaminopyridine, immunosuppression&bull;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Myasthenia gravis",
"       </td>",
"       <td>",
"        IV immunoglobulin, plasma exchange, immunosuppression, anticholinesterases",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Dermatomyositis",
"       </td>",
"       <td>",
"        IV immunoglobulin, immunosuppression",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Opsoclonus-myoclonus (pediatric)",
"       </td>",
"       <td>",
"        IV immunoglobulin, steroids, ACTH, rituximab",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Neuropathy associated with osteosclerotic myeloma",
"       </td>",
"       <td>",
"        Radiation, chemotherapy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Anti-NMDAR",
"       </td>",
"       <td>",
"        <p>",
"         First line: Corticosteroids, IV immunoglobin, plasma exchange",
"        </p>",
"        <p>",
"         Second line: Cyclophosphamide, rituximab",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Anti-LGI1",
"       </td>",
"       <td>",
"        Corticosteroids, IV immunoglobin, plasma exchange",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Anti-cell surface antigens other than NMDAR and LGI1 (AMPA receptor, GABA(B) receptor, CASPR2)",
"       </td>",
"       <td>",
"        Corticosteroids, IVIg, plasma exchange, cyclophosphamide",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        Syndromes that may respond to treatment*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Stiff-man syndrome",
"       </td>",
"       <td>",
"        IV immunoglobulin, steroids, diazepam, baclofen",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Neuromyotonia",
"       </td>",
"       <td>",
"        IV immunoglobulin, plasma exchange, phenytoin, carbamazepine",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Guillain-Barre (Hodgkin's lymphoma)",
"       </td>",
"       <td>",
"        IV immunoglobulin, plasma exchange",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Vasculitis of nerve and muscle",
"       </td>",
"       <td>",
"        Steroids, cyclophosphamide",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Limbic encephalitis",
"       </td>",
"       <td>",
"        IV immunoglobulin, steroids",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Opsoclonus-myoclonus (adults)",
"       </td>",
"       <td>",
"        Steroids, cyclophosphamide, protein A column, clonazepam, thiamine",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Anti-MAG-associated peripheral neuropathy (Waldenstroms macroglbulinemia)",
"       </td>",
"       <td>",
"        IV immunoglobulin, plasma exchange, chlorambucil, cyclophosphamide, fludarabine, rituximab",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Acute necrotizing myopathy",
"       </td>",
"       <td>",
"        Immunosuppression",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        Syndromes that usually do not respond to treatment*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" colspan=\"2\">",
"        Encephalomyelitis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" colspan=\"2\">",
"        Sensory neuronopathy&Delta;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" colspan=\"2\">",
"        Autonomic dysfunction",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" colspan=\"2\">",
"        Cerebellar degeneration",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" colspan=\"2\">",
"        Cancer and melanoma-associated retinopathy",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * In all cases, initial treatment should focus on identifying and treating the tumor.",
"     <br>",
"      <span class=\"bullet\">",
"       &bull;",
"      </span>",
"      Immunsuppression includes corticosteroids or azothioprine.",
"      <br>",
"       &Delta; Rare partial response to steroids.",
"      </br>",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced with permission from: Bataller L, Dalmau J. Paraneoplastic neurologic syndromes: Approaches to diagnosis and treatment. Semin Neurol 2003; 23:215. Copyright &copy; 2003 Thieme.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
