" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Clinical features and diagnosis of the mucopolysaccharidoses",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?7/6/7274/contributors\">",
"     Authors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?7/6/7274/contributors\">",
"     Emil Kakkis, MD, PhD",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?7/6/7274/contributors\">",
"     Ed Wraith, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?7/6/7274/contributors\">",
"     Section Editors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?7/6/7274/contributors\">",
"     Sihoun Hahn, MD, PhD",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?7/6/7274/contributors\">",
"     Marc C Patterson, MD, FRACP",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?7/6/7274/contributors\">",
"     Deputy Editor",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?7/6/7274/contributors\">",
"     Elizabeth TePas, MD, MS",
"    </a>",
"    <br/>",
"   </div>",
"  </div>",
