var script_f7_60_8129=[" ",
"",
"",
"function log(i){",
"return Math.log(i) * Math.LOG10E;   ",
"}",
"",
"function ln(i){",
"return Math.log(i);",
"}",
"",
"function sq(i){",
"return i * i;",
"}",
"",
"function sqr(i){",
"return Math.sqrt(i);",
"}",
"",
"",
"function power(x,y){",
"return Math.pow(x,y);",
"}",
"",
"function eTo(x){",
"return Math.exp(x);",
"}",
"",
"",
"function fixDP(r, dps) {",
"if (isNaN(r)) return \"NaN\";",
"var msign = '';",
"if (r < 0) msign = '-';",
"x = Math.abs(r);",
"if (x > Math.pow(10, 21)) return msign + x.toString();",
"var m = Math.round(x * Math.pow(10, dps)).toString();",
"if (dps == 0) return msign + m;",
"while (m.length <= dps) m = \"0\" + m;",
"return msign + m.substring(0, m.length - dps) + \".\" + m.substring(m.length - dps);",
"}",
"",
"function alertNaN(thisparam){",
"alert(thisparam + ' is improperly formatted. You may only input the digits 0-9 and a decimal point.');  ",
"doCalc = false; ",
"clrResults();",
"}",
"",
"var currenttimeout;",
"",
"",
"var calctxt = ''; ",
"var xmltxt = ''; ",
"var htmtxt = ''; ",
"",
"",
"",
"function MaintenanceFluidChildren_fx() {",
"",
"currenttimeout = self.setTimeout('minMaxCheck();', 2000);",
"",
"with(document.MaintenanceFluidChildren_form){",
"",
"",
"doCalc = true;",
"param_value = parseFloat(Weight_param.value);",
"if (isNaN(param_value)){param_value = \"\"; doCalc = false;}",
"unit_parts = Weight_unit.options[Weight_unit.selectedIndex].value.split('|');",
"Weight = param_value * parseFloat(unit_parts[0]) + parseFloat(unit_parts[1]);",
"dp = 0;",
"if(Weight < 10) Daily_Vol = 100 * Weight;",
"if((Weight >= 10) && (Weight < 20 )) Daily_Vol = 1000 + (50 * (Weight - 10));",
"if(Weight >= 20) Daily_Vol = 1500 + (20 * (Weight - 20));",
"if (Daily_Vol > 2400) Daily_Vol = 2400;",
"daily_vol_result.value = Math.round(10 * Daily_Vol) / 10;",
"Fluid_Rate =  Daily_Vol / 24;",
"",
"unit_parts = Fluid_Rate_unit.options[Fluid_Rate_unit.selectedIndex].value.split('|');",
"if (doCalc) Fluid_Rate_param.value = fixDP((Fluid_Rate - parseFloat(unit_parts[1])) / parseFloat(unit_parts[0]), dp);",
"",
"",
"",
"",
"",
"}",
"",
"}",
"",
"function minMaxCheck(){",
"",
"if (currenttimeout) self.clearTimeout(currenttimeout);",
"",
"with(document.MaintenanceFluidChildren_form){",
"",
"if (Weight_param.value && isNaN(Weight_param.value)){ Weight_param.value = ''; alertNaN('Weight'); }",
"if (Weight_param.value && Weight < 3.499) {",
"Weight = 0;",
"Weight_param.value = \"\";",
"clrResults();",
"doCalc = false;",
"alert(\"The minimum value for Weight is 3.499 kg.\\nIf you are specifying a value with a different unit,\\nchange the unit selector first.\");",
"}",
"if (Weight_param.value && Weight > 200) {",
"Weight_param.value = \"\";",
"clrResults();",
"Weight = 0;",
"doCalc = false;",
"alert(\"The maximum value for Weight is 200 kg.\\nIf you are specifying a value with a different unit,\\nchange the unit selector first.\");",
"}",
"",
"",
"}",
"",
"}",
"",
"",
"function clrResults(){",
"",
"if (currenttimeout) self.clearTimeout(currenttimeout);",
"",
"with(document.MaintenanceFluidChildren_form){",
"",
"daily_vol_result.value = '';",
"Fluid_Rate_param.value = '';",
"",
"",
"}",
"",
"}",
"",
"",
"",
"",
"",
"",
"",
"",
"",
"",
"",
""].join("\n");
