" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Stenotrophomonas maltophilia",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?7/60/8135/contributors\">",
"     Authors",
"    </a>",
