"        <sup>",
"         [3]",
"        </sup>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Bleomycin",
"       </td>",
"       <td>",
"        10 units*/m",
"        <sup>",
"         2",
"        </sup>",
"        IV on day 8",
"       </td>",
"       <td rowspan=\"7\">",
"        Repeat cycle every 21 days",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Etoposide",
"       </td>",
"       <td>",
"        100 mg/m",
"        <sup>",
"         2",
"        </sup>",
"        IV on days 1 through 3",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Doxorubicin",
"       </td>",
"       <td>",
"        25 mg/m",
"        <sup>",
"         2",
"        </sup>",
"        IV on day 1",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Cyclophosphamide&Delta;",
"       </td>",
"       <td>",
"        650 mg/m",
"        <sup>",
"         2",
"        </sup>",
"        IV on day 1",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Vincristine",
"       </td>",
"       <td>",
"        1.4 mg/m",
"        <sup>",
"         2",
"        </sup>",
"        (maximum 2 mg) IV on day 8",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Procarbazine",
"       </td>",
"       <td>",
"        100 mg/m",
"        <sup>",
"         2",
"        </sup>",
"        oral on days 1 through 7",
