var title_f7_61_8155="Phenytoin concentrations and clinical signs";
var content_f7_61_8155=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=EM%2F61755&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=EM%2F61755&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Phenytoin toxicity: Approximate correlation of serum concentration and clinical signs",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Serum concentration",
"       </td>",
"       <td class=\"subtitle1\">",
"        Clinical findings",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &lt;10 mg/L (40 &micro;mol/L)",
"       </td>",
"       <td>",
"        None",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        10-20 mg/L&nbsp;(40-80 &micro;mol/L)&nbsp;",
"        <strong>",
"         (therapeutic)",
"        </strong>",
"       </td>",
"       <td>",
"        Mild nystagmus",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        20-30 mg/L (80-120 &micro;mol/L)",
"       </td>",
"       <td>",
"        Nystagmus",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        30-40 mg/L (120-160 &micro;mol/L)",
"       </td>",
"       <td>",
"        Ataxia, slurred speech, nausea, vomiting",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        40-50 mg/L (160-200 &micro;mol/L)",
"       </td>",
"       <td>",
"        Lethargy and confusion",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &gt;50 mg/L (&gt;200 &micro;mol/L)",
"       </td>",
"       <td>",
"        Coma",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
