var title_f7_63_8189="Hymenoptera characteristics";
var content_f7_63_8189=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ALLRG%2F66768&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ALLRG%2F66768&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Characteristics of Hymenoptera (bees and wasps)",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Common name",
"       </td>",
"       <td class=\"subtitle1\">",
"        Typical number of adults in a nest",
"       </td>",
"       <td class=\"subtitle1\">",
"        Solitary, but many can occur in the same area",
"       </td>",
"       <td class=\"subtitle1\">",
"        Will sting in defense of the nest",
"       </td>",
"       <td class=\"subtitle1\">",
"        Will not sting unless pinched or handled",
"       </td>",
"       <td class=\"subtitle1\">",
"        Nest",
"       </td>",
"       <td class=\"subtitle1\">",
"        Principal food",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"7\">",
"        Bees",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Honey bee",
"       </td>",
"       <td>",
"        Thousands",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        &radic;",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        Wax combs in hollow trees, building voids, or manmade hives; Africanized honey bees will nest in many types of cavities",
"       </td>",
"       <td>",
"        Pollen and honey made from nectar",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Bumble bee",
"       </td>",
"       <td>",
"        Dozens",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        &radic;",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        Wax pots in the ground or other cavities",
"       </td>",
"       <td>",
"        Pollen and honey made from nectar",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Carpenter bee",
"       </td>",
"       <td>",
"        One",
"       </td>",
"       <td>",
"        &radic;",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        &radic;",
"       </td>",
"       <td>",
"        Tunnels in wood trim, fences, and decks",
"       </td>",
"       <td>",
"        Pollen and nectar",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Sweat bee",
"       </td>",
"       <td>",
"        One",
"       </td>",
"       <td>",
"        &radic;",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        &radic;",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        Pollen and nectar",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"7\">",
"        Wasps",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Yellowjackets",
"       </td>",
"       <td>",
"        Hundreds to thousands",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        &radic;",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        Paper combs hidden by envelope and attached to vegetation or buildings, or in the ground or building voids",
"       </td>",
"       <td>",
"        Insects, nectar, ripe fruit; some species scavenge carrion and human food",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Paper wasps",
"       </td>",
"       <td>",
"        Dozens",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        &radic;",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        A single, exposed (no envelope) paper comb with cells open at the bottom",
"       </td>",
"       <td>",
"        Insects (often caterpillars), nectar",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        European hornet",
"       </td>",
"       <td>",
"        Hundreds",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        &radic;",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        Brittle, brown combs, with or without envelope, in hollow trees or building voids",
"       </td>",
"       <td>",
"        Insects (often bees and yellowjackets), ripe fruit, sap",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Cicada killers",
"       </td>",
"       <td>",
"        One",
"       </td>",
"       <td>",
"        &radic;",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        &radic;",
"       </td>",
"       <td>",
"        Burrows in the ground",
"       </td>",
"       <td>",
"        Cicadas",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Mud daubers",
"       </td>",
"       <td>",
"        One",
"       </td>",
"       <td>",
"        &radic;",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        &radic;",
"       </td>",
"       <td>",
"        Mud lumps or tubes in sheltered areas",
"       </td>",
"       <td>",
"        Spiders",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
