" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Gastric volvulus in adults",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?7/7/7289/contributors\">",
"     Author",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?7/7/7289/contributors\">",
"     Jon O Wee, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?7/7/7289/contributors\">",
"     Section Editor",
"    </a>",
