var content_f7_8_7309=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PULM%2F63674&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PULM%2F63674&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Granulomatous diseases of nose and paranasal sinuses",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <colgroup span=\"2\" width=\"50%\">",
"     </colgroup>",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Granulomatous diseases",
"       </td>",
"       <td class=\"subtitle1\">",
"        Diagnostic tests",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        Infections",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2_start\" colspan=\"2\">",
"        <em>",
"         Mycobacterial",
"        </em>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        Tuberculosis (M. tuberculosis)",
"       </td>",
"       <td>",
"        PPD, CXR, QFT-G, AFB smear and culture",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        Leprosy (M. leprae)",
"       </td>",
"       <td>",
"        Skin smear or biopsy, PCR",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        Atypical mycobacterial (eg, M. chelonei)",
"       </td>",
"       <td>",
"        AFB smear and culture",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2_start\" colspan=\"2\">",
"        <em>",
"         Treponemal",
"        </em>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        Syphilis",
"       </td>",
"       <td>",
"        RPR, FTA-ABS",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2_start\" colspan=\"2\">",
"        <em>",
"         Bacterial",
"        </em>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        Rhinoscleroma (Klebsiella rhinoscleromatis)",
"       </td>",
"       <td>",
"        Culture",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2_start\" colspan=\"2\">",
"        <em>",
"         Fungal",
"        </em>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        Histoplasmosis (Histoplasma capsulatum)",
"       </td>",
"       <td>",
"        Special stains, culture, serology",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2_start\" colspan=\"2\">",
"        <em>",
"         Protozoan",
"        </em>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        Leishmaniasis (Leishmania braziliensis)",
"       </td>",
"       <td>",
"        Skin smear or aspirate, PCR, biopsy showing intra and extracellular Leishman bodies",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        Rhinosporidiosis (Rhinosporidium seeberi)",
"       </td>",
"       <td>",
"        Biopsy showing granulomas and typical sporangia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        Vasculitides",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Granulomatosis with polyangiitis (Wegener's)",
"       </td>",
"       <td>",
"        ANCA, biopsy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Churg-Strauss syndrome",
"       </td>",
"       <td>",
"        Peripheral eosinophilia, ANCA, biopsy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        Malignancy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Lethal midline granulomatosis (extranodal NK/T cell lymphoma)",
"       </td>",
"       <td>",
"        Biopsy showing polymorphous lymphoid infiltrate; immunophenotype positive for CD2, surface CD3, CD56; clonal EBV genomes present",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Nonkeratinizing nasopharyngeal carcinoma",
"       </td>",
"       <td>",
"        Biopsy shows an epithelial neoplasm with a dense lymphoplasmacytic infiltrate (predominantly T cells); associated with EBV infection",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        Other",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Sarcoidosis",
"       </td>",
"       <td>",
"        Biopsy showing noncaseating granulomas, CXR",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Cocaine-induced midline destructive lesion",
"       </td>",
"       <td>",
"        History of cocaine use",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Granulomatous rosacea",
"       </td>",
"       <td>",
"        Granulomatous perifolliculitis",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     PPD: purified protein derivative skin test; CXR: chest radiograph; QFT-G: Quantiferon-Gold; AFB: acid fast bacilli; PCR: polymerase chain reaction; RPR: rapid plasma reagin; FTA-ABS: fluorescent treponemal antibody absorption; ANCA: antineutrophil cytoplasmic antibody; EBV: Epstein Barr virus.",
"    </div>",
"    <div class=\"reference\">",
"     Data from: Zargari O, Elpern DJ. Granulomatous diseases of the nose. Int J Dermatol 2009; 48:1275.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
var script_f7_8_7309=[""].join("\n");
