var title_f7_8_7310="Genetic forms dystonia";
var content_f7_8_7310=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=NEURO%2F56658&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=NEURO%2F56658&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Genetic forms of dystonia",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <colgroup span=\"5\" width=\"20%\">",
"     </colgroup>",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Locus",
"       </td>",
"       <td class=\"subtitle1\">",
"        Designation",
"       </td>",
"       <td class=\"subtitle1\">",
"        Mode of inheritance",
"       </td>",
"       <td class=\"subtitle1\">",
"        Clinical features",
"       </td>",
"       <td class=\"subtitle1\">",
"        Chromosome, mutation and gene product",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        DYT 1",
"       </td>",
"       <td>",
"        Early onset dystonia",
"       </td>",
"       <td>",
"        AD",
"       </td>",
"       <td>",
"        Typically begins in childhood; onset in limb often with progression to generalized dystonia",
"       </td>",
"       <td>",
"        9q34; GAG deletion in TOR1A gene causes abnormality in ATP-binding protein, torsinA",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        DYT2",
"       </td>",
"       <td>",
"        Autosomal recessive dystonia",
"       </td>",
"       <td>",
"        AR",
"       </td>",
"       <td>",
"        Childhood onset, generalized or segmental; Gypsy families",
"       </td>",
"       <td>",
"        Unknown chromosome, gene product",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        DYT3",
"       </td>",
"       <td>",
"        X-linked dystonia-parkinsonism (Lubag)",
"       </td>",
"       <td>",
"        X-linked recessive",
"       </td>",
"       <td>",
"        Segmental or generalized dystonia with parkinsonism; predominantly in males from Panay island in Philippines",
"       </td>",
"       <td>",
"        Xq13.1; TAF1 gene",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        DYT4",
"       </td>",
"       <td>",
"        Whispering dysphonia",
"       </td>",
"       <td>",
"        AD",
"       </td>",
"       <td>",
"        Laryngeal and cervical dystonia described in one Australian family",
"       </td>",
"       <td>",
"        Chromosomal location, gene and product unknown",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"2\">",
"        DYT5",
"       </td>",
"       <td>",
"        Dopa-responsive dystonia/parkinsonism",
"       </td>",
"       <td>",
"        AD",
"       </td>",
"       <td>",
"        Dystonia with or without parkinsonism; dramatic response to levodopa",
"       </td>",
"       <td>",
"        14q22; GCH1 locus; mutations in GTP cyclohydrolase I gene",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Segawa syndrome",
"       </td>",
"       <td>",
"        AR",
"       </td>",
"       <td>",
"        Dystonia with or without parkinsonism; diurnal variation, with worst symptoms in evening; marked response to levodopa",
"       </td>",
"       <td>",
"        11p15.5; mutations in the tyrosine hydroxylase gene; tyrosine hydroxylase deficiency",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        DYT6",
"       </td>",
"       <td>",
"        Adolescent onset dystonia of mixed type",
"       </td>",
"       <td>",
"        AD",
"       </td>",
"       <td>",
"        Mostly segmental dystonia; mixed limb, cervical, cranial; Amish-Mennonite, German, Irish, Italian families",
"       </td>",
"       <td>",
"        8p11.21; mutations in the THAP1 gene",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        DYT7",
"       </td>",
"       <td>",
"        Adult onset focal dystonia",
"       </td>",
"       <td>",
"        AD",
"       </td>",
"       <td>",
"        Adult onset with cervical dystonia, limb dystonia, dysphonia, or blepharospasm; German family",
"       </td>",
"       <td>",
"        18p; gene and product unknown",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        DYT8",
"       </td>",
"       <td>",
"        Paroxysmal nonkinesigenic dyskinesia 1",
"       </td>",
"       <td>",
"        AD",
"       </td>",
"       <td>",
"        Episodes of dystonia/choreoathetosis not precipitated by exercise or activity",
"       </td>",
"       <td>",
"        2q35; myofibrillogenesis regulator (MR1 gene)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        DYT9 (allelic with DYT18)",
"       </td>",
"       <td>",
"        Paroxysmal choreoathetosis with episodic spasticity and ataxia",
"       </td>",
"       <td>",
"        AD",
"       </td>",
"       <td>",
"        Episodes of dystonia, diplopia, and paresthesia with spastic paraplegia between attacks",
"       </td>",
"       <td>",
"        1p34.2; mutations in the SLC2A1 gene that encodes the glucose transporter type 1",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        DYT10",
"       </td>",
"       <td>",
"        Episodic kinesigenic dyskinesia 1 (paroxysmal kinesigenic dyskinesia)",
"       </td>",
"       <td>",
"        AD",
"       </td>",
"       <td>",
"        Episodic choreoathetosis and dystonia brought on by exercise/activity",
"       </td>",
"       <td>",
"        16p11.2; mutations in the PRRT2 gene",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        DYT11",
"       </td>",
"       <td>",
"        Myoclonus-dystonia",
"       </td>",
"       <td>",
"        AD",
"       </td>",
"       <td>",
"        Myoclonic jerks associated with variable features of dystonia; very alcohol responsive",
"       </td>",
"       <td>",
"        7q; epsilon-sarcoglycan gene (SGCE)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        DYT12",
"       </td>",
"       <td>",
"        Rapid onset dystonia- parkinsonism",
"       </td>",
"       <td>",
"        AD",
"       </td>",
"       <td>",
"        Onset of dystonia and parkinsonism over days to months",
"       </td>",
"       <td>",
"        19q; ATP1A3 gene that encodes the Na+/K+ -ATPase alpha3 subunit",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        DYT13",
"       </td>",
"       <td>",
"        Multifocal and segmental dystonia",
"       </td>",
"       <td>",
"        AD",
"       </td>",
"       <td>",
"        Early adult onset segmental dystonia with cervical, cranial, and arm dystonia",
"       </td>",
"       <td>",
"        1p; gene and product unknown",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        DYT14",
"       </td>",
"       <td>",
"        Dopa responsive dystonia (now included under DYT5)",
"       </td>",
"       <td>",
"        AD",
"       </td>",
"       <td>",
"        Dystonia responsive to dopamine",
"       </td>",
"       <td>",
"        14q; gene and product unknown",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        DYT15",
"       </td>",
"       <td>",
"        Myoclonus-dystonia",
"       </td>",
"       <td>",
"        AD",
"       </td>",
"       <td>",
"        Myoclonus and dystonia",
"       </td>",
"       <td>",
"        18p; gene and product unknown",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        DYT16",
"       </td>",
"       <td>",
"        Early onset dystonia-parkinsonism",
"       </td>",
"       <td>",
"        AR",
"       </td>",
"       <td>",
"        Early onset generalized dystonia; some cases with parkinsonism unresponsive to levodopa",
"       </td>",
"       <td>",
"        2q31.3; PRKRA gene",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        DYT17",
"       </td>",
"       <td>",
"        Adolescent onset",
"       </td>",
"       <td>",
"        AR",
"       </td>",
"       <td>",
"        Onset in neck with regional spread",
"       </td>",
"       <td>",
"        20p11.2-q13.12; gene and product unknown",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        DYT18 (allelic with DYT9)",
"       </td>",
"       <td>",
"        Paroxysmal choreoathetosis with episodic spasticity and ataxia",
"       </td>",
"       <td>",
"        AD",
"       </td>",
"       <td>",
"        Episodes of dystonia, diplopia, paresthesias with spastic paraplegia between attacks",
"       </td>",
"       <td>",
"        1p34.2; mutations in the SLC2A1 gene that encodes the glucose transporter type 1",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        DYT19",
"       </td>",
"       <td>",
"        Episodic kinesigenic dyskinesia&nbsp;2 (paroxysmal kinesigenic dyskinesia)",
"       </td>",
"       <td>",
"        AD",
"       </td>",
"       <td>",
"        Episodic choreoathetosis and dystonia brought on by exercise/activity",
"       </td>",
"       <td>",
"        16q13-q22.1; gene and product unknown",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        DYT20",
"       </td>",
"       <td>",
"        Paroxysmal nonkinesigenic dyskinesia 2",
"       </td>",
"       <td>",
"        ?",
"       </td>",
"       <td>",
"        Spontaneous episodes of dystonia",
"       </td>",
"       <td>",
"        2q31; gene and product unknown",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        DYT21",
"       </td>",
"       <td>",
"        Adolescent to adult onset",
"       </td>",
"       <td>",
"        AD",
"       </td>",
"       <td>",
"        Generalized or multifocal",
"       </td>",
"       <td>",
"        2q14.3-q21.3; gene and product unknown",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     AD: autosomal dominant; AR: autosomal recessive; ATP: adenosine triphosphate.",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
