" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Indications for administration of anti-(D) immune globulin",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td>",
"        At 28 weeks of gestation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Spontaneous abortion, threatened abortion, induced abortion",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Ectopic pregnancy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Invasive procedures: genetic amniocentesis; chorionic villus sampling; multi-fetal reduction; fetal blood sampling",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Hydatidiform mole",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Fetal death in the second or third trimester",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Blunt trauma to the abdomen",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Antepartum hemorrhage in the second or third trimester (eg, placenta previa or abruption)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        External cephalic version",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
