var title_f8_13_8413="SCOPER categorization system";
var content_f8_13_8413=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PULM%2F85891&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=4\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PULM%2F85891&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=4\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    SCOPER categorization system",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <colgroup span=\"6\" width=\"16%\">",
"     </colgroup>",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Sleep",
"       </td>",
"       <td class=\"subtitle1\">",
"        Cardiovascular",
"       </td>",
"       <td class=\"subtitle1\">",
"        Oximetry",
"       </td>",
"       <td class=\"subtitle1\">",
"        Position",
"       </td>",
"       <td class=\"subtitle1\">",
"        Effort",
"       </td>",
"       <td class=\"subtitle1\">",
"        Respiratory",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        S",
"        <sub>",
"         1",
"        </sub>",
"        - Sleep by 3 EEG channels* with EOG and chin EMG",
"       </td>",
"       <td>",
"        C",
"        <sub>",
"         1",
"        </sub>",
"        - More than 1 ECG lead - can derive events",
"       </td>",
"       <td>",
"        O",
"        <sub>",
"         1",
"        </sub>",
"        - Oximetry (finger or ear) with recommended sampling",
"       </td>",
"       <td>",
"        P",
"        <sub>",
"         1",
"        </sub>",
"        - Video or visual position measurement",
"       </td>",
"       <td>",
"        E",
"        <sub>",
"         1",
"        </sub>",
"        - 2 RIP belts",
"       </td>",
"       <td>",
"        R",
"        <sub>",
"         1",
"        </sub>",
"        - Nasal pressure and thermal device",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        S",
"        <sub>",
"         2",
"        </sub>",
"        &nbsp;- Sleep by less than 3 EEG channels* with or without EOG or chin EMG",
"       </td>",
"       <td>",
"        C",
"        <sub>",
"         2",
"        </sub>",
"        - Peripheral arterial tonometry",
"       </td>",
"       <td>",
"        O",
"        <sub>",
"         1x",
"        </sub>",
"        - Oximetry (finger or ear) without recommended sampling (per Scoring Manual) or not described",
"       </td>",
"       <td>",
"        P",
"        <sub>",
"         2",
"        </sub>",
"        - Non-visual position measurement",
"       </td>",
"       <td>",
"        E",
"        <sub>",
"         2",
"        </sub>",
"        - 1 RIP belt",
"       </td>",
"       <td>",
"        R",
"        <sub>",
"         2",
"        </sub>",
"        - Nasal pressure",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        S",
"        <sub>",
"         3",
"        </sub>",
"        - Sleep surrogate: eg, actigraphy",
"       </td>",
"       <td>",
"        C",
"        <sub>",
"         3",
"        </sub>",
"        - Standard ECG measure (1 lead)",
"       </td>",
"       <td>",
"        O",
"        <sub>",
"         2",
"        </sub>",
"        - Oximetry with alternative site (eg, forehead)",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        E",
"        <sub>",
"         3",
"        </sub>",
"        - Derived effort (eg, forehead versus pressure)",
"       </td>",
"       <td>",
"        R",
"        <sub>",
"         3",
"        </sub>",
"        - Thermal device",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        S",
"        <sub>",
"         4",
"        </sub>",
"        - Other sleep measure",
"       </td>",
"       <td>",
"        C",
"        <sub>",
"         4",
"        </sub>",
"        - Derived pulse (typically from oximetry)",
"       </td>",
"       <td>",
"        O",
"        <sub>",
"         3",
"        </sub>",
"        - Other oximetry",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        E",
"        <sub>",
"         4",
"        </sub>",
"        - Other effort measure (including piezo belts)",
"       </td>",
"       <td>",
"        R",
"        <sub>",
"         4",
"        </sub>",
"        - End-Tidal CO",
"        <sub>",
"         2",
"        </sub>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        C",
"        <sub>",
"         5",
"        </sub>",
"        - Other cardiac measure",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        R",
"        <sub>",
"         5",
"        </sub>",
"        - Other respiratory measure",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    Proper oximetry sampling is defined as 3 s averaging and a minimum of 10 Hz sampling rate (25 Hz desirable)",
"    <sup>",
"     [1]",
"    </sup>",
"    .",
"    <div class=\"footnotes\">",
"     EEG: electroencephalography; EOG: electrooculography; EMG: electromyography; ECG: electrocardiography; RIP: respiratory inductance plethysmography.",
"     <br>",
"      * 3 EEG channels defined as frontal, central, and occipital.",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"     References:",
"     <br/>",
"     <ol>",
"      <li>",
"       Iber C, Ancoli-Israel S, Chesson A, Quan SF. The AASM manual for the scoring of sleep and associated events: rules, terminology and technical specifications, 1st ed. American Academy of Sleep Medicine, Westchester, IL 2007.",
"      </li>",
"     </ol>",
"     Reproduced with permission from: Collop NA, Tracy SL, Kapur V, et al. Obstructive sleep apnea devices for out-of-center (OOC) testing: technology evaluation. J Clin Sleep Med 2011; 7:531. Copyright &copy; 2011 American Academy of Sleep Medicine.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
