var title_f8_15_8444="Vaccine true contraindications";
var content_f8_15_8444=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ID%2F75067&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ID%2F75067&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    True contraindications to vaccine administration",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Safety concern",
"       </td>",
"       <td class=\"subtitle1\">",
"        Reason for contraindication and recommended action",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Previous anaphylactic reaction to a specific vaccine",
"       </td>",
"       <td>",
"        Avoid revaccination with the specific vaccine because of risk of recurrence",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        History of anaphylaxis to eggs or egg-protein",
"       </td>",
"       <td>",
"        Avoid measles, mumps, influenza and yellow-fever vaccine because these vaccines are prepared in embryonated chicken eggs or cultures and vaccines may contain residual egg protein",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Previous anaphylactic reaction to neomycin or streptomycin",
"       </td>",
"       <td>",
"        Avoid measles, mumps, rubella (MMR) vaccine because the MMR vaccine contains trace amounts of neomycin",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        History of severe systemic reactions to the cholera, typhoid or plague vaccine",
"       </td>",
"       <td>",
"        Avoid revaccination with the specific vaccine because of risk of recurrence",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Adults who are immunocompromised as a result of disease or its treatment",
"       </td>",
"       <td>",
"        Avoid live virus vaccines because there is an increased risk of viral replication in immunocompromised individual [See related card]",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Household members of immunocompromised patients",
"       </td>",
"       <td>",
"        Avoid oral polio because vaccine induced disease (if it occurs) could be transmitted to the immunocompromised individual. This concern does not apply to the MMR vaccine because infection with vaccine strain measles, mumps or rubella is not transmitted to others [See related card]",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Pregnant women",
"       </td>",
"       <td>",
"        Avoid all live virus vaccines because of the potential risk to the fetus [See related card]",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Data from: American College Physicians Task Force on Adult Immunization and Infectious Diseases Society of America. Guide for adult immunization. Philadelphia. American College of Physicians, 1994; Centers for Disease Control and Prevention. MMWR Morb Mortal Wkly Rep 1994; 43:1; and Gershon, AA, Gardner, P, Peter, G, et al. Clin Infect Dis 1997; 25:782.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
