var title_f8_24_8588="Physiologic effects of high altitude";
var content_f8_24_8588=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=EM%2F63999&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=EM%2F63999&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Physiologic effects of high altitude",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        High altitude: 1500 to 3500 m (4921-11,483 ft)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        High-altitude illness common with abrupt ascent to above 2500 m (8202 ft)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Decreased exercise performance and increased ventilation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Minor impairment in SpO",
"        <sub>",
"         2",
"        </sub>",
"        , usually at least 90 percent; PaO",
"        <sub>",
"         2",
"        </sub>",
"        significantly diminished 55 to 75 mmHg",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Very high altitude: 3500 to 5500 m (11,483-18,045 ft)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Most common range for severe high-altitude illness",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Abrupt ascent may be dangerous; requires a period of acclimatization",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        SpO",
"        <sub>",
"         2",
"        </sub>",
"        75 to 85 percent; PaO",
"        <sub>",
"         2",
"        </sub>",
"        40 to 60 mmHg",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Extreme hypoxia may occur during sleep, exercise and high-altitude illness",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Extreme altitude: 5500 to 8850 m (18,045-29,035 ft)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Progressive deterioration of physiologic function eventually outstrips acclimatization",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Above the highest permanent human habitation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Abrupt ascent almost always precipitates severe high-altitude illness",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        A period of acclimatization necessary to ascend to extreme altitude",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Severe hypoxia and hypocapnia; SpO",
"        <sub>",
"         2",
"        </sub>",
"        58 to 75 percent, PaO",
"        <sub>",
"         2",
"        </sub>",
"        28 to 40 mmHg",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     SaO",
"     <sub>",
"      2",
"     </sub>",
"     : arterial oxygen saturation; PaO",
"     <sub>",
"      2",
"     </sub>",
"     : arterial PO",
"     <sub>",
"      2",
"     </sub>",
"     ; PO",
"     <sub>",
"      2",
"     </sub>",
"     : partial pressure of oxygen.",
"    </div>",
"    <div class=\"reference\">",
"     Data from: Hackett, PH, Roach, RC. High-Altitude Medicine. In: Wilderness Medicine, 5th ed, Auerbach, PS (Ed), Mosby, Philadelphia 2007.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
