" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Acquired torticollis in children",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?8/26/8616/contributors\">",
"     Authors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?8/26/8616/contributors\">",
"     Charles G Macias, MD, MPH",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?8/26/8616/contributors\">",
"     Vanthaya Gan, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?8/26/8616/contributors\">",
"     Section Editors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?8/26/8616/contributors\">",
"     Richard G Bachur, MD",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?8/26/8616/contributors\">",
"     William Phillips, MD",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?8/26/8616/contributors\">",
"     Ellen M Friedman, MD",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?8/26/8616/contributors\">",
"     Glenn C Isaacson, MD, FAAP",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?8/26/8616/contributors\">",
"     Deputy Editor",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?8/26/8616/contributors\">",
"     James F Wiley, II, MD, MPH",
"    </a>",
"    <br/>",
"   </div>",
"  </div>",
