var title_f8_31_8699="Use of SCOOP system I";
var content_f8_31_8699=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PULM%2F63083&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=9\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PULM%2F63083&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=9\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Patient management tips for the SCOOP transtracheal oxygen system - I",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1\">",
"       Phase",
"      </td>",
"      <td class=\"subtitle1\">",
"       Goal",
"      </td>",
"      <td class=\"subtitle1\">",
"       Management strategy",
"      </td>",
"     </tr>",
"     <tr>",
"      <td colspan=\"1\" rowspan=\"2\">",
"       I",
"      </td>",
"      <td>",
"       Patient selection",
"      </td>",
"      <td>",
"       Select patients who are enthusiastic and desire to be active.The patient must be willing to assume responsibility for the catheter or have assistance from a significant other.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Patient orientation",
"      </td>",
"      <td>",
"       Reinforce verbal instructions by showing a videotape and giving the patient a booklet and article to review. Give the patient the phone number of another transtracheal patient to call to ask questions or ask the transtracheal patient to place the call.",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from Hoffman LA, Wesmiller SW.Transtracheal oxygen. In: Portable Oxygen Therapy: Including Oxygen Conserving Methodology, Tiep BL (Ed), Mount Kisco, NY, 1991.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
