var title_f8_31_8700="Types of moisturizers";
var content_f8_31_8700=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F72872&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F72872&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Types of moisturizers",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Action",
"       </td>",
"       <td class=\"subtitle1\">",
"        Mechanism of action",
"       </td>",
"       <td class=\"subtitle1\">",
"        Indication",
"       </td>",
"       <td class=\"subtitle1\">",
"        Examples",
"       </td>",
"       <td class=\"subtitle1\">",
"        Comments",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Occulsive",
"       </td>",
"       <td>",
"        Blocks transepidermal water loss",
"        <br/>",
"        Barrier",
"       </td>",
"       <td>",
"        Xerosis",
"        <br/>",
"        Atopic dermatitis",
"        <br/>",
"        Prevention of irritant contact dermatitis",
"       </td>",
"       <td>",
"        Petrolatum",
"        <br/>",
"        Zinc oxide",
"        <br/>",
"        Lanolin",
"        <br/>",
"        Mineral oil",
"        <br/>",
"        Silicones",
"       </td>",
"       <td>",
"        Messy, comedogenic",
"        <br/>",
"        Cosmetically unacceptable",
"        <br/>",
"        May cause folliculitis (mineral oil) or contact dermatitis (lanolin)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Humectant",
"       </td>",
"       <td>",
"        Attracts water to stratum corneum",
"       </td>",
"       <td>",
"        Xerosis",
"        <br/>",
"        Icthyosis",
"       </td>",
"       <td>",
"        Urea",
"        <br/>",
"        Alpha hydroxy acids",
"        <br/>",
"        Glycerin",
"        <br/>",
"        Sorbital",
"        <br/>",
"        Lactic acid",
"       </td>",
"       <td>",
"        May cause irritation (urea, lactic acid)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Emollient",
"       </td>",
"       <td>",
"        Smooths skin",
"       </td>",
"       <td>",
"        Decrease skin roughness",
"       </td>",
"       <td>",
"        Cholesterol",
"        <br/>",
"        Squalene",
"        <br/>",
"        Fatty acids",
"       </td>",
"       <td>",
"        Not always effective",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Replacement of deficiencies in \"raw materials\" of the intact stratum corneum",
"       </td>",
"       <td>",
"        Claims to replenish essential skin components",
"       </td>",
"       <td>",
"        Possibly skin rejuvenation",
"       </td>",
"       <td>",
"        <p>",
"         Ceramides, natural moisturizing factor",
"        </p>",
"       </td>",
"       <td>",
"        May improve skin moisturization and barrier function, but unproven",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from Lynde, CW. Moisturizers: what they are and how they work. Skin Therapy Lett 2001; 6:3.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
