" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Arsenic exposure and poisoning",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?8/33/8730/contributors\">",
