var title_f8_34_8748="Asbestosis histologic grades";
var content_f8_34_8748=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PULM%2F56557&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PULM%2F56557&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Histologic grades of asbestosis",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Grade",
"       </td>",
"       <td class=\"subtitle1\">",
"        Change",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\" rowspan=\"1\">",
"        Grade",
"of severity",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        0",
"       </td>",
"       <td>",
"        No fibrosis associated with bronchioles",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        1 or I",
"       </td>",
"       <td>",
"        Early fibrosis involving walls of at least one",
"respiratory bronchiole, with or without extension into septa of",
"adjacent alveoli; fibrosis confined to alveolated walls of respiratory",
"bronchioles and ducts and not present in more distant alveoli.",
"Alveolitis and inflammation similar to that caused by cigarette smoking",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        2 or II",
"       </td>",
"       <td>",
"        More severe fibrosis involving acinus: alveolar ducts",
"and/or two or more layers of adjacent alveoli. Normal lung remains in a",
"zone between adjacent bronchioles",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        3 or III",
"       </td>",
"       <td>",
"        Fibrosis advanced and coalescent, involves entire",
"acinus; all lung between at least two adjacent bronchioles is affected.",
"Some alveoli are completely obliterated",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        4 or IV",
"       </td>",
"       <td>",
"        Honeycomb remodeling and large (up to 1 cm) dilated",
"spaces grossly visible in parenchyma",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\" rowspan=\"1\">",
"        Grade",
"of extent",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        A or 1",
"       </td>",
"       <td>",
"        Only occasional bronchioles are involved. Most appear",
"normal",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        B or 2",
"       </td>",
"       <td>",
"        <span style=\"font-weight: bold;\">",
"         \"",
"        </span>",
"        More",
"than occasional\" but less than half of bronchioles are involved",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        C or 3",
"       </td>",
"       <td>",
"        More than half of bronchioles are involved",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     Developed in 1980 by a committee of the College of American Pathologists.",
"    </div>",
"    <div class=\"reference\">",
"     Am J Respir Crit Care Med vol 170 pp 692, 2004; www.atsjournals.org.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
