" <div class=\"graphic\">",
"  <div class=\"figure\" style=\"width: 505px\">",
"   <div class=\"ttl\">",
"    Ultrasound of acardiac twin",
"   </div>",
"   <div class=\"cntnt\" style=\"width: 485px; height: 345px; background-image: url(data:image/jpeg;base64,);\">",
"   </div>",
"   <div class=\"lgnd\">",
