" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Clinical syndromes of stunned or hibernating myocardium",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?8/35/8759/contributors\">",
"     Author",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?8/35/8759/contributors\">",
"     David M Shavelle, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?8/35/8759/contributors\">",
"     Section Editors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?8/35/8759/contributors\">",
"     Bernard J Gersh, MB, ChB, DPhil, FRCP, MACC",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?8/35/8759/contributors\">",
"     Jeroen J Bax, MD, PhD",
"    </a>",
"    <br/>",
"   </div>",
