var title_f8_35_8763="Medication overuse headache criteria";
var content_f8_35_8763=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=NEURO%2F71798&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=NEURO%2F71798&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Revised International Headache Society criteria for medication overuse headache",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td>",
"        A. Headache present on &ge;15 days a month",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        B. Regular overuse for more than three months of one or more acute/symptomatic treatment drugs",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        1. Ergotamine, triptans, opioids,",
"        <strong>",
"         or",
"        </strong>",
"        combination analgesic medications on &ge;10 days a month on a regular basis for more than three months",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        2. Simple analgesics",
"        <strong>",
"         or",
"        </strong>",
"        any combination of ergotamine, triptans, analgesic opioids on &ge;15 days a month on a regular basis for more than three months without overuse of any single class alone",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        C. Headache has developed or markedly worsened during medication overuse",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Olesen J, Bousser MG, Diener HC, et al. New appendix criteria open for a broader concept of chronic migraine. Cephalalgia 2006; 26:742. Copyright &copy; 2006 Sage Publications. Reprinted by permission of Sage.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
