var title_f8_39_8827="LEVO LV dose adjustment";
var content_f8_39_8827=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ONC%2F64297&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ONC%2F64297&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Recommendations for dose adjustment with LEVOleucovorin (l-isomer of leucovorin) for prolonged methotrexate excretion",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1\">",
"       Plasma methotrexate (MTX) level",
"      </td>",
"      <td class=\"subtitle1\">",
"       Dose and/or schedule adjustment for l-leucovorin (LEVO leucovorin)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       &lt;50 micromolar at 24 hours",
"      </td>",
"      <td>",
"       7.5 mg IV every 6 hours",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       &ge;50 micromolar at 24 hours",
"       <strong>",
"        OR",
"       </strong>",
"      </td>",
"      <td rowspan=\"3\">",
"       <p>",
"        75 mg IV every 3 hours until MTX level &lt;1 micromolar.",
"       </p>",
"       <p>",
"        Then, 7.5 mg IV every 3 hours until MTX level &lt;0.05 micromolar.",
"       </p>",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       &ge;5 micromolar at 48 hours",
"       <strong>",
"        OR",
"       </strong>",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       100 percent or greater increase in serum creatinine level at 24 hours after MTX administration",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
