var title_f8_41_8860="Aspiration pneumonia categories";
var content_f8_41_8860=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ID%2F66505&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ID%2F66505&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Classification of aspiration pneumonia",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Inoculum",
"       </td>",
"       <td class=\"subtitle1\">",
"        Pulmonary sequelae",
"       </td>",
"       <td class=\"subtitle1\">",
"        Clinical features",
"       </td>",
"       <td class=\"subtitle1\">",
"        Therapy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Acid",
"       </td>",
"       <td>",
"        Chemical pneumonitis",
"       </td>",
"       <td>",
"        <p>",
"         Acute dyspnea, tachpnea, tachycardia; &plusmn; cyanosis, bronchospasm, fever",
"        </p>",
"        <p>",
"         Sputum: pink, frothy",
"        </p>",
"        <p>",
"         Radiograph: infiltrates in one or both lower lobes",
"        </p>",
"        <p>",
"         Hypoxemia",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         Positive pressure breathing",
"        </p>",
"        <p>",
"         Intravenous fluids",
"        </p>",
"        <p>",
"         Tracheal suction",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Oropharyngeal bacteria",
"       </td>",
"       <td>",
"        Bacterial infection",
"       </td>",
"       <td>",
"        <p>",
"         Usually insidious onset",
"        </p>",
"        <p>",
"         Cough, fever, purulent sputum",
"        </p>",
"        <p>",
"         Radiograph: infiltrate involving dependent pulmonary segment or lobe &plusmn; cavitation",
"        </p>",
"       </td>",
"       <td>",
"        Antibiotics",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Inert fluids",
"       </td>",
"       <td>",
"        <p>",
"         Mechanical obstruction",
"        </p>",
"        <p>",
"         Reflex airway closure",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         Acute dyspnea, cyanosis &plusmn; apnea",
"        </p>",
"        <p>",
"         Pulmonary edema",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         Tracheal suction",
"        </p>",
"        <p>",
"         Intermittent positive-pressure breathing with oxygen and isoproterenol",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Particulate matter",
"       </td>",
"       <td>",
"        Mechanical obstruction",
"       </td>",
"       <td>",
"        Dependent upon level of obstruction, ranging from acute apnea and rapid death to irritative chronic cough &plusmn; recurrent infections",
"       </td>",
"       <td>",
"        <p>",
"         Extraction of particulate matter",
"        </p>",
"        <p>",
"         Antibiotics for superimposed infection",
"        </p>",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
