var title_f8_45_8924="Binet staging system CLL";
var content_f8_45_8924=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=HEME%2F80421%7EHEME%2F56284&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=8\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=HEME%2F80421%7EHEME%2F56284&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=8\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Binet Staging System For Chronic Lymphocytic Leukemia",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1\">",
"       Stage",
"      </td>",
"      <td class=\"subtitle1\">",
"       Description",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       A",
"      </td>",
"      <td>",
"       Two or less lymphoid bearing areas enlarged*",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       B",
"      </td>",
"      <td>",
"       Three or more lymphoid bearing areas enlarged*",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       C",
"      </td>",
"      <td>",
"       Presence of anemia (Hgb &lt;10.0 g/dL) or thrombocytopenia (platelet count &lt;100,000/microL)",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * Five lymphoid bearing areas are possible: cervical, axillary, inguino-femoral, spleen, and liver.",
"    </div>",
"    <div class=\"reference\">",
"     Revised from Binet, JL, Auquier, A, Dighiero, G, et al. Cancer 1981; 48:198.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Modified Rai clinical staging system for chronic lymphocytic leukemia",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Risk",
"       </td>",
"       <td class=\"subtitle1\">",
"        Stage",
"       </td>",
"       <td class=\"subtitle1\">",
"        Description",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Low",
"       </td>",
"       <td>",
"        0",
"       </td>",
"       <td>",
"        Lymphocytosis in blood or bone marrow",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"1\" rowspan=\"2\">",
"        Intermediate",
"       </td>",
"       <td>",
"        I",
"       </td>",
"       <td>",
"        Lymphocytosis + enlarged lymph nodes",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        II",
"       </td>",
"       <td>",
"        Lymphocytosis + enlarged liver or spleen with or",
"without lymphadenopathy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"1\" rowspan=\"2\">",
"        High",
"       </td>",
"       <td>",
"        III",
"       </td>",
"       <td>",
"        Lymphocytosis + anemia (Hgb &lt;11 g/dL)",
"with&nbsp;or without enlarged liver, spleen, or lymph lnodes",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        IV",
"       </td>",
"       <td>",
"        Lymphocytosis + thrombocytopenia (platelet count",
"&lt;100,000/&micro;L) with or without anemia or enlarged liver,",
"spleen, or lymph nodes",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
