var title_f8_47_8956="Criteria for normal labor";
var content_f8_47_8956=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=OBGYN%2F76118&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=8\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=OBGYN%2F76118&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=8\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Diagnostic criteria for abnormal patterns in active labor",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Labor pattern",
"       </td>",
"       <td class=\"subtitle1\">",
"        Nullipara",
"       </td>",
"       <td class=\"subtitle1\">",
"        Multipara",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"3\">",
"        First stage",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Duration (no anesthesia)",
"       </td>",
"       <td>",
"        16.6 hours",
"       </td>",
"       <td>",
"        12.5 hours",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Duration (anesthesia)",
"       </td>",
"       <td>",
"        19.0 hours",
"       </td>",
"       <td>",
"        14.9 hours",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Protracted dilation",
"       </td>",
"       <td>",
"        &lt;1.2 cm/h",
"       </td>",
"       <td>",
"        &lt;1.5 cm/h",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Arrested dilation",
"       </td>",
"       <td>",
"        &gt;2 h",
"       </td>",
"       <td>",
"        &gt;2 h",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"3\">",
"        Second stage",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Duration (no anesthesia)",
"       </td>",
"       <td>",
"        132 minutes",
"       </td>",
"       <td>",
"        61 minutes",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Duration (anesthesia)",
"       </td>",
"       <td>",
"        185 minutes",
"       </td>",
"       <td>",
"        131 minutes",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Arrest of descent (epidural)",
"       </td>",
"       <td>",
"        &gt;3 h",
"       </td>",
"       <td>",
"        &gt;2 h",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Arrest of descent (no epidural)",
"       </td>",
"       <td>",
"        &gt;2 h",
"       </td>",
"       <td>",
"        &gt;1 h",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    Values represent approximately two standard deviations from the mean.",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Data compiled from: Kilpatrick SJ, Laros, RK. Obstet gynecol 1989; 74:85 and Friedman, EA, ed. Labor clinical evaluation and management. 2nd ed New York. Appleton-Century &mdash; Crofts, 1978 and American College of Obstetrics and Gynecology Committee on Practice Bulletins-Obstetrics. ACOG Practice Bulletin Number 49, December 2003: Dystocia and augmentation of labor. Obstet Gynecol 2003; 102:1445.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
