var title_f8_49_8989="Fetal alcohol syndrome - Diagnostic criteria";
var content_f8_49_8989=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F53028&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F53028&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Fetal alcohol syndrome - Diagnostic criteria",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Facial dysmorphia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Based on racial norms, individual exhibits all three characteristic facial features:",
"        <br/>",
"        <ul>",
"         <li>",
"          Smooth philtrum (University of Washington Lip-Philtrum Guide rank 4 or 5)",
"         </li>",
"         <li>",
"          Thin vermillion border (University of Washington Lip-Philtrum Guide rank 4 or 5)",
"         </li>",
"         <li>",
"          Small palpebral fissures (at or below 10",
"          <sup>",
"           th",
"          </sup>",
"          percentile)",
"         </li>",
"        </ul>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Growth problems",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Confirmed prenatal or postnatal height or weight, or both, at or below the 10",
"        <sup>",
"         th",
"        </sup>",
"        percentile, documented at any one point in time (adjusted for age, sex, gestational age, and race or ethnicity)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Central nervous system abnormalities",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Structural",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Head circumference (OFC) at or below the 10",
"        <sup>",
"         th",
"        </sup>",
"        percentile adjusted for age and sex",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Clinically significant brain abnormalities observable through imaging",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Neurological",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Neurological problems not due to a postnatal insult or fever, or other soft neurological signs outside normal limits",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Functional",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Performance substantially below that expected for an individual's age, schooling, or circumstances, as evidenced by:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        1. Global cognitive or intellectual deficits representing multiple domains of deficit (or significant developmental delay in younger children) with performance below the 3",
"        <sup>",
"         rd",
"        </sup>",
"        percentile (two standard deviations below the mean for standardized testing)",
"        <strong>",
"         OR",
"        </strong>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        2. Functional deficits below the 16",
"        <sup>",
"         th",
"        </sup>",
"        percentile (one standard deviation below the mean for standardized testing) in at least three of the following domains:",
"        <br/>",
"        <ul>",
"         <li>",
"          Cognitive or developmental deficits or discrepancies",
"         </li>",
"         <li>",
"          Executive functioning deficits",
"         </li>",
"         <li>",
"          Motor functioning delays",
"         </li>",
"         <li>",
"          Problems with attention or hyperactivity",
"         </li>",
"         <li>",
"          Social skills",
"         </li>",
"         <li>",
"          Other, such as sensory problems, pragmatic language problems, memory deficits, etc",
"         </li>",
"        </ul>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Maternal alcohol exposure",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Confirmed prenatal alcohol exposure",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Unknown prenatal alcohol exposure",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Criteria for fetal alcohol syndrome diagnosis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Requires all three of the following findings:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <ul>",
"         <li>",
"          Documentation of all three facial abnormalities (smooth philtrum, thin vermillion border, and small palpebral fissures)",
"         </li>",
"         <li>",
"          Documentation of growth deficits",
"         </li>",
"         <li>",
"          Documentation of central nervous system (CNS) abnormality",
"         </li>",
"        </ul>",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced from: The National Center on Birth Defects and Developmental Disabilities, Centers for Disease Control and Prevention. Fetal Alcohol Syndrome: Guidelines for Referral and Diagnosis. Full report available at: file://www.cdc.gov/ncbddd/fasd/documents/FAS_guidelines_accessible.pdf. (Accessed on July 11, 2011).",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
