" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Approach to the adult patient with syncope in the emergency department",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?8/52/9034/contributors\">",
"     Authors",
"    </a>",
"    <br/>",
