"function GestationalAgeFromEDD_fx() {",
"",
"currenttimeout = self.setTimeout('minMaxCheck();', 3000);",
"",
"with(document.GestationalAgeFromEDD_form){",
"",
"",
"doCalc = true;",
"dp = 0;",
"monthname = new Array('Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sep', 'Oct', 'Nov', 'Dec');",
"edd_time = new Date;",
"current_time = new Date;",
"var current_month_value = parseFloat(current_month.options[current_month.selectedIndex].value);",
"var current_date_value = parseFloat(current_date.options[current_date.selectedIndex].value);",
"var current_year_value = parseFloat(current_year.options[current_year.selectedIndex].value);",
"if ((current_month_value == 1) && (current_date_value > 28)){",
"if ((current_year_value % 4) == 0){ current_date_value = 29; current_date.selectedIndex = 28; }",
"else{ current_date_value = 28; current_date.selectedIndex = 27; };",
"}",
"if (((current_month_value == 3) || (current_month_value == 5) || (current_month_value == 8) || (current_month_value == 10)) && (current_date_value > 30)){ current_date_value = 30; current_date.selectedIndex = 29; };",
"current_time.setFullYear(current_year_value);",
"current_time.setMonth(current_month_value, current_date_value);",
"current_time.setHours(0);",
"var edd_month_value = parseFloat(edd_month.options[edd_month.selectedIndex].value);",
"var edd_date_value = parseFloat(edd_date.options[edd_date.selectedIndex].value);",
"var edd_year_value = parseFloat(edd_year.options[edd_year.selectedIndex].value);",
"if ((edd_month_value == 1) && (edd_date_value > 28)){",
"if ((edd_year_value % 4) == 0){ edd_date_value = 29; edd_date.selectedIndex = 28; }",
"else{ edd_date_value = 28; edd_date.selectedIndex = 27; };",
"}",
"if (((edd_month_value == 3) || (edd_month_value == 5) || (edd_month_value == 8) || (edd_month_value == 10)) && (edd_date_value > 30)){ edd_date_value = 30; edd_date.selectedIndex = 29; };",
"edd_time.setFullYear(edd_year_value);",
"edd_time.setMonth(edd_month_value, edd_date_value);",
"edd_time.setHours(0);",
"geddmillis = current_time.getTime() - edd_time.getTime() + 24192000000;",
"geddweeks = fixDP(geddmillis / 604800000, 1);",
"geddweeksfloor = Math.floor(geddweeks);",
"gedddays =  Math.round(7 * (geddweeks - geddweeksfloor));",
"daystxt = ' days';",
"if (gedddays == 1) daystxt = ' day';",
"if ((edd_year_value >= 0) && (edd_month_value >= 0) && (edd_date_value >= 0)){ gest_edd.value = geddweeksfloor + ' weeks' + ' ' + gedddays + daystxt;}",
"else { gest_edd.value = ''; }",
"",
"",
"}",
"",
"}",
