var title_f8_55_9084="Risk for high altitude illness";
var content_f8_55_9084=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=EM%2F65647&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=EM%2F65647&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Risk for high altitude illness",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Risk of HAI",
"       </td>",
"       <td class=\"subtitle1\">",
"        Description",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"3\">",
"        Low",
"       </td>",
"       <td>",
"        No prior history of altitude illness and planning ascent to &lt;2800 m",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Taking two days or more to arrive at 2500 to 3000 m from low altitude",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Ascending no more than 500 m/day (sleeping altitude) once over 2500 m",
"        <strong>",
"         and",
"        </strong>",
"        taking one extra day to acclimatize for every additional 1000 m of ascent",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td rowspan=\"3\">",
"        Moderate",
"       </td>",
"       <td>",
"        Prior history of AMS and ascending to 2500 to 2800 m in less than two days",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        No history of AMS and ascending to 2800 m or higher in less than two days",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Ascending &gt;500 m/day (increase in sleeping elevation) at altitudes above 3000 m",
"        <strong>",
"         with",
"        </strong>",
"        one extra day for acclimatization for every additional 1000 m of ascent",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td rowspan=\"5\">",
"        High",
"       </td>",
"       <td>",
"        History of severe altitude illness (HACE, HAPE)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        History of AMS and ascending to 2800 m or higher in less than two days",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Ascending over 3500 m in less than two days",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Ascending &gt;500 m/day (increase in sleeping elevation) above 3000 m without extra days for acclimatization; rapid guided ascents (eg, Mt. Kilimanjaro in &lt;7 days)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Persons with medical conditions predisposing to altitude illness",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    Notes:",
"    <br>",
"     Altitudes listed refer to the altitude at which the person sleeps.",
"     <br>",
"      Ascent is assumed to begin at low altitude (&lt;1200m).",
"      <br>",
"       Risk categories are for unacclimatized persons.",
"       <div class=\"footnotes\">",
"        AMS: acute mountain sickness; HACE: high altitude cerebral edema; HAPE: high altitude pulmonary edema; m: meters.",
"       </div>",
"       <div class=\"reference\">",
"       </div>",
"      </br>",
"     </br>",
"    </br>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
