var title_f8_57_9115="Hunt and Hess grading system";
var content_f8_57_9115=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=NEURO%2F69179&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=NEURO%2F69179&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Hunt and Hess grading system for patients with subarachnoid hemorrhage",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Grade",
"       </td>",
"       <td class=\"subtitle1\">",
"        Neurologic status",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        1",
"       </td>",
"       <td>",
"        Asymptomatic or mild headache and slight nuchal rigidity",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        2",
"       </td>",
"       <td>",
"        Severe headache, stiff neck, no neurologic deficit except cranial nerve palsy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        3",
"       </td>",
"       <td>",
"        Drowsy or confused, mild focal neurologic deficit",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        4",
"       </td>",
"       <td>",
"        Stuporous, moderate or severe hemiparesis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        5",
"       </td>",
"       <td>",
"        Coma, decerebrate posturing",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Based upon initial neurologic examination; adapted from: Hunt W, Hess R, J Neurosurg 1968; 28:14.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
