var title_f8_57_9116="Management of unstable aortic dissection";
var content_f8_57_9116=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=CARD%2F60961&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=CARD%2F60961&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    European Society of Cardiology guidelines: management of hemodynamically unstable patients with suspected aortic dissection",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Recommendation",
"       </td>",
"       <td class=\"subtitle1_single\">",
"        Class",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        1. Profound hemodynamic instability: intubation and ventilation.",
"       </td>",
"       <td>",
"        I",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        2. Transesophageal echocardiography as the sole diagnostic procedure; call surgeon.",
"       </td>",
"       <td>",
"        II",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        3. Surgery, based on findings of cardiac tamponade by transthoracic echocardiography.",
"       </td>",
"       <td>",
"        II",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        4. Pericardiocentesis (lowers intrapericardial pressure, may cause recurrent bleeding).",
"       </td>",
"       <td>",
"        III",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\" colspan=\"2\" rowspan=\"1\">",
"        Classification",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"2\" rowspan=\"1\">",
"        <strong>",
"         Class I:",
"        </strong>",
"        Conditions for which there is evidence and/or general agreement that a given procedure or treatment is useful and effective.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\" colspan=\"2\" rowspan=\"1\">",
"        <strong>",
"         Class II:",
"        </strong>",
"        Conditions for which there is conflicting evidence and/or a divergence of opinion about the usefulness/efficacy of a procedure or treatment.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\" colspan=\"2\" rowspan=\"1\">",
"        <strong>",
"         Class IIa:",
"        </strong>",
"        Weight of evidence/opinion is in favor of usefulness/efficacy.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\" colspan=\"2\" rowspan=\"1\">",
"        <strong>",
"         Class IIb:",
"        </strong>",
"        Usefulness/efficacy less well established by evidence/opinion.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"2\" rowspan=\"1\">",
"        <strong>",
"         Class III:",
"        </strong>",
"        Conditions for which there is evidence and/or general agreement that the procedure/treatment is not useful and in some cases may be harmful.&nbsp;",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Data reproduced with permission from Erbel, R, Alfonso, F, Boileau, C, et al, Eur Heart J 2001; 22:1642.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
