" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Severity of acute graft-versus-host-disease by organ system",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <colgroup span=\"2\" width=\"10%\">",
"     </colgroup>",
"     <colgroup width=\"80%\">",
"     </colgroup>",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Organ",
"       </td>",
"       <td class=\"subtitle1\">",
"        Grade",
"       </td>",
"       <td class=\"subtitle1\">",
"        Description",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"4\">",
"        Skin",
"       </td>",
"       <td class=\"centered\">",
"        +1",
"       </td>",
"       <td>",
"        Maculopapular rash over &lt;25 percent of body area",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"centered\">",
"        +2",
"       </td>",
"       <td>",
"        Maculopapular rash over 25 to 50 percent of body area",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"centered\">",
"        +3",
"       </td>",
"       <td>",
"        Generalized erythroderma",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"centered\">",
"        +4",
"       </td>",
"       <td>",
"        Generalized erythroderma with bullous formation and often with desquamation",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td rowspan=\"4\">",
"        Liver",
"       </td>",
"       <td class=\"centered\">",
"        +1",
"       </td>",
"       <td>",
"        Bilirubin 2.0 to 3.0 mg/dL; SGOT 150 to 750 IU",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"centered\">",
"        +2",
"       </td>",
"       <td>",
"        Bilirubin 3.1 to 6.0 mg/dL",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"centered\">",
"        +3",
"       </td>",
"       <td>",
"        Bilirubin 6.1 to 15.0 mg/dL",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"centered\">",
"        +4",
"       </td>",
"       <td>",
"        Bilirubin &gt;15.0 mg/dL",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td rowspan=\"4\">",
"        Gut",
"       </td>",
"       <td class=\"centered\">",
"        +1",
"       </td>",
"       <td>",
"        Diarrhea &gt;30 mL/kg or &gt;500 mL/day",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"centered\">",
"        +2",
"       </td>",
"       <td>",
"        Diarrhea &gt;60 mL/kg or &gt;1000 mL/day",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"centered\">",
"        +3",
"       </td>",
"       <td>",
"        Diarrhea &gt;90 mL/kg or &gt;1500 mL/day",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"centered\">",
"        +4",
"       </td>",
"       <td>",
"        Diarrhea &gt;90 mL/kg or &gt;2000 mL/day; or severe abdominal pain with or without ileus",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
