var title_f8_7_8315="Lung preservation storage";
var content_f8_7_8315=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PULM%2F79611&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=7\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PULM%2F79611&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=7\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Donor lung preservation during storage",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td>",
"       <strong>",
"        Ventilation",
"       </strong>",
"      </td>",
"      <td>",
"       <p>",
"        Inflate lung(s) to 50 percent total lung capacity or 20 cm H2O of airway peak pressure; then clamp trachea",
"       </p>",
"       <p>",
"        Use an FIO2 of 30 to 50 percent",
"       </p>",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       <strong>",
"        Preservation solution",
"       </strong>",
"      </td>",
"      <td>",
"       Low potassium dextran (Perfadex&reg;)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       <strong>",
"        Temperature",
"       </strong>",
"      </td>",
"      <td>",
"       4 to 8&deg;C",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       <strong>",
"        Topical bath solution",
"       </strong>",
"      </td>",
"      <td>",
"       Low potassium dextran (Perfadex&reg;)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       <strong>",
"        Ischemic time",
"       </strong>",
"      </td>",
"      <td>",
"       Less than 8 hours preferred, possibly up to 12",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
