var title_f8_7_8316="ACC AHA Rx HF normal LVEF";
var content_f8_7_8316=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=CARD%2F70468&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=CARD%2F70468&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    ACC/AHA/ESC guideline summary: Management of patients with heart failure (HF) and preserved systolic function",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Class I - There is evidence and/or general agreement that the following approaches are effective in the management of HF and preserved left ventricular systolic function",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull; Control of hypertension, both systolic and diastolic, according to current guidelines.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull; Control of ventricular rate in atrial fibrillation.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull; Control of pulmonary congestion and peripheral edema with diuretics.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Class IIa - The weight of evidence or opinion is in favor of the usefulness of the following approach in the management of HF and preserved left ventricular systolic function",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull; Coronary revascularization for symptomatic or asymptomatic myocardial ischemia that is thought to have an adverse effect of cardiac function.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Class IIb - The weight of evidence or opinion is less well established for the usefulness of the following approaches in the management of HF and preserved left ventricular systolic function",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull; Restoration of sinus rhythm to improve diagnosis in patients with atrial fibrillation.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull; Among patients controlled hypertension, use of beta blockers, angiotensin converting enzyme inhibitors, angiotensin II receptor blockers, and calcium channel blockers in an attempt to reduce symptoms of HF.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull; Digitalis to reduce symptoms of HF.",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Data from: Hunt SA, Abraham WT, Chin MH, et al. ACC/AHA 2005 Guideline Update for the Diagnosis and Management of Chronic Heart Failure in the Adult: a report of the American College of Cardiology/American Heart Association Task Force on Practice Guidelines (Writing Committee to Update the 2001 Guidelines for the Evaluation and Management of Heart Failure): developed in collaboration with the American College of Chest Physicians and the International Society for Heart and Lung Transplantation: endorsed by the Heart Rhythm Society. Circulation 2005; 112:e154.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
