" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Epidemiology, clinical manifestations, and diagnosis of genital herpes simplex virus infection",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?9/10/9386/contributors\">",
"     Author",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?9/10/9386/contributors\">",
"     Mary A Albrecht, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?9/10/9386/contributors\">",
"     Section Editor",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?9/10/9386/contributors\">",
"     Martin S Hirsch, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?9/10/9386/contributors\">",
"     Deputy Editor",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?9/10/9386/contributors\">",
"     Anna R Thorner, MD",
"    </a>",
"    <br/>",
"   </div>",
"  </div>",
